func:
const data = msg.payload;

// Dispositivo 1: DHT22
if (data.devices && data.devices.dht22) {
    msg.dht22 = {
        temperature: data.devices.dht22.temperature,
        humidity: data.devices.dht22.humidity,
        status: data.devices.dht22.status
    };
}

// Dispositivo 2: Solo
if (data.devices && data.devices.soilSensor) {
    msg.soil = {
        moisture: data.devices.soilSensor.moisture_percent,
        raw: data.devices.soilSensor.moisture_raw,
        status: data.devices.soilSensor.status
    };
}

// Dispositivo 3: Chuva
if (data.devices && data.devices.rainSensor) {
    msg.rain = {
        intensity: data.devices.rainSensor.intensity_percent,
        raw: data.devices.rainSensor.rain_raw,
        status: data.devices.rainSensor.status
    };
}

// Dispositivo 4: Buzzer
if (data.devices && data.devices.buzzer) {
    msg.buzzer = {
        active: data.devices.buzzer.active,
        alert_level: data.devices.buzzer.alert_level
    };
}

// Dispositivo 5: LED Status
if (data.devices && data.devices.statusLED) {
    msg.led = {
        state: data.devices.statusLED.state,
        color: data.devices.statusLED.color,
        pattern: data.devices.statusLED.blink_pattern
    };
}

// An√°lise de Risco
if (data.analysis) {
    msg.analysis = data.analysis;
}

// Informa√ß√µes do sistema
msg.system = {
    farmId: data.farmId,
    teamName: data.teamName,
    location: data.location,
    timestamp: data.timestamp
};

return msg;

if (!msg.analysis) return null;

const analysis = msg.analysis;
const soil = msg.soil ? msg.soil.moisture : 0;
const rain = msg.rain ? msg.rain.intensity : 0;
const temp = msg.dht22 ? msg.dht22.temperature : 0;

// Criar mensagem de risco
let riskColor = '#00b500'; // Verde
let riskIcon = '‚úÖ';

if (analysis.risk_level >= 8) {
    riskColor = '#ca3838'; // Vermelho
    riskIcon = 'üö®';
} else if (analysis.risk_level >= 6) {
    riskColor = '#ff8c00'; // Laranja
    riskIcon = '‚ö†Ô∏è';
} else if (analysis.risk_level >= 4) {
    riskColor = '#e6e600'; // Amarelo
    riskIcon = 'üü°';
}

msg.riskData = {
    level: analysis.risk_level,
    description: analysis.risk_description,
    recommendation: analysis.recommendation,
    color: riskColor,
    icon: riskIcon,
    floodAlert: analysis.flood_alert,
    droughtAlert: analysis.drought_alert,
    extremeWeatherAlert: analysis.extreme_weather_alert
};

// Dados para gr√°fico hist√≥rico
msg.chartData = {
    timestamp: new Date().toLocaleTimeString(),
    soil: soil,
    rain: rain,
    risk: analysis.risk_level,
    temperature: temp
};

return msg;

// Preparar dados para o gr√°fico
if (!msg.chartData) return null;

const data = msg.chartData;

// Criar m√∫ltiplas s√©ries para o gr√°fico
const series = [
    {
        series: 'Solo (%)',
        x: data.timestamp,
        y: data.soil
    },
    {
        series: 'Chuva (%)',
        x: data.timestamp, 
        y: data.rain
    },
    {
        series: 'Risco (x10)',
        x: data.timestamp,
        y: data.risk * 10
    },
    {
        series: 'Temp (¬∞C)',
        x: data.timestamp,
        y: data.temperature
    }
];

return series.map(s => ({payload: [s]}));

const alerts = msg.payload;
if (!alerts || !alerts.active_alerts) return null;

let message = `üö® ALERTA ${alerts.severity}\n`;
message += `Farm: ${alerts.farmId}\n`;
message += `Local: ${alerts.location}\n\n`;

alerts.active_alerts.forEach(alert => {
    message += `${alert.type}: ${alert.message}\n`;
});

message += `\nA√ß√£o: ${alerts.recommendation}`;

msg.payload = message;
return msg;

// Monitorar status dos 5 dispositivos
if (!msg.payload || !msg.payload.devices) return null;

const devices = msg.payload.devices;
let statusReport = {
    timestamp: new Date().toISOString(),
    totalDevices: 5,
    activeDevices: 0,
    deviceStatus: {}
};

// Verificar cada dispositivo
if (devices.dht22) {
    statusReport.deviceStatus.dht22 = devices.dht22.status;
    if (devices.dht22.status === 'active') statusReport.activeDevices++;
}

if (devices.soilSensor) {
    statusReport.deviceStatus.soilSensor = devices.soilSensor.status;
    statusReport.activeDevices++;
}

if (devices.rainSensor) {
    statusReport.deviceStatus.rainSensor = devices.rainSensor.status;
    statusReport.activeDevices++;
}

if (devices.buzzer) {
    statusReport.deviceStatus.buzzer = devices.buzzer.active ? 'active' : 'standby';
    statusReport.activeDevices++;
}

if (devices.statusLED) {
    statusReport.deviceStatus.statusLED = devices.statusLED.state ? 'active' : 'inactive';
    statusReport.activeDevices++;
}

statusReport.systemHealth = (statusReport.activeDevices / statusReport.totalDevices) * 100;

msg.deviceStatus = statusReport;
return msg;
